import * as SQLite from 'expo-sqlite'

const DATABASE_NAME='churchapp.db';

const data =require('../../assets/bibliaData.json'); 

export  function getDbConnection() {
    
    const db=   SQLite.openDatabase(DATABASE_NAME);
    return db;

}

export  function dropTablaHimnos(db) {
    db.transaction(tx => {
        console.log('Esta entrando a dropTablaHimnos');
        tx.executeSql('DROP TABLE IF EXISTS HIMNOS');
        tx.executeSql('DROP TABLE IF EXISTS biblia');
 
      });
    }

export  function createTable(db) {
    db.transaction(tx => {
        console.log('Esta entrando a crear tabla');
        tx.executeSql('CREATE TABLE IF NOT EXISTS HIMNOS (id INTEGER PRIMARY KEY AUTOINCREMENT,HOLD Int,HNEW int,title varchar,himno varchar,favorite int)');
        tx.executeSql('CREATE TABLE IF NOT EXISTS biblia (id INTEGER PRIMARY KEY AUTOINCREMENT,Libro varchar,Capitulo varchar,VersoN varchar,Verso varchar,Nlibro int)');
       
 
      });
     

}


export async function loadBibleData(db) {
  return new Promise((resolve, reject) => {
    db.transaction(
      (tx) => {
 
        console.log('Esta entrando a insert biblia');
        tx.executeSql(
          'SELECT COUNT(*) as count FROM biblia',
          [],
          (_, { rows }) => {
            const rowCount = rows.item(0).count;
            if (rowCount === 0) {
              // La tabla está vacía, inserta valores
    
                  data.forEach((item) => {
                    tx.executeSql(
                      'INSERT INTO biblia (Libro, Capitulo,VersoN,Verso,Nlibro) VALUES (?, ?, ?, ?, ?)',
                      [item.Libro, item.Capitulo,item.VersoN,item.Verso,item.Nlibro],
                      (_, result) => {
                        // Éxito al insertar
                        console.log('Insertado con éxito: ', item.Libro);
                      },
                      (_, error) => {
                        // Error al insertar
                        console.error('Error al insertar: ', error);
                      }
                    );
                  });
                } else {
                  
                  console.log('La tabla ya tiene datos');
                }
              },
              (_, error) => {
                console.error('Error al contar registros en la tabla bibla', error);
              }
            );
      },
      (error) => {
        // Error en la transacción
        reject(error);
      },
      () => {
        // Éxito en la transacción
        resolve();
      }
    );
  });
}


 

